-- Add indexes for better query performance on large dataset
-- Check if indexes exist first, then create if not
CREATE INDEX IF NOT EXISTS idx_protocols_county ON protocolChunks(countyId);
CREATE INDEX IF NOT EXISTS idx_protocols_section ON protocolChunks(section);
CREATE INDEX IF NOT EXISTS idx_protocols_number ON protocolChunks(protocolNumber);
CREATE INDEX IF NOT EXISTS idx_protocols_year ON protocolChunks(protocolYear);
CREATE INDEX IF NOT EXISTS idx_counties_state ON counties(state);

-- Show current table sizes
SELECT 
    'protocolChunks' as table_name,
    COUNT(*) as row_count
FROM protocolChunks
UNION ALL
SELECT 
    'counties' as table_name,
    COUNT(*) as row_count
FROM counties;

